package com.ailk.utils;import org.htmlparser.Parser;import org.htmlparser.util.NodeList;import org.htmlparser.util.ParserException;import org.htmlparser.visitors.HtmlPage;public class Htmlmover {	public static void main(String[] args) {		NodeList rt = getNodeList("http://forex.hexun.com/rmbhl/");		System.out.println(rt.toHtml());	}	public static NodeList getNodeList(String url) {		Parser parser = null;		HtmlPage visitor = null;		try {			parser = new Parser(url);			parser.setEncoding("GBK");			visitor = new HtmlPage(parser);			parser.visitAllNodesWith(visitor);		} catch (ParserException e) {			e.printStackTrace();		}		NodeList nodeList = visitor.getBody();		return nodeList;	}}